const iconElement=document.querySelector(".weather-icon"),tempElement=document.querySelector(".temperature-value p"),descElement=document.querySelector(".temperature-description p"),locationElement=document.querySelector(".location p"),weather={temperature:{unit:"celsius"}},kelvin=273,key="85726e6724f4693e1671c2c805b23087",latitude=18.42,longitude=-64.62;let api="http://api.openweathermap.org/data/2.5/weather?lat=18.42&lon=-64.62&appid="+key;function displayWeather(){iconElement.innerHTML=`<img src="../wp-content/themes/bvisupco/images/icons/${weather.iconId}.png"/>`,tempElement.innerHTML=weather.temperature.value+"°<span>C</span>",descElement.innerHTML=weather.description,locationElement.innerHTML=`${weather.city}, ${weather.country}`}function celsiusToFahrenheit(e){return 9*e/5+32}fetch(api).then((function(e){return e.json()})).then((function(e){weather.temperature.value=Math.floor(e.main.temp-273),weather.description=e.weather[0].description,weather.iconId=e.weather[0].icon,weather.city=e.name,weather.country=e.sys.country})).then((function(){displayWeather()})),tempElement.addEventListener("click",(function(){if(void 0!==weather.temperature.value)if("celsius"==weather.temperature.unit){let e=celsiusToFahrenheit(weather.temperature.value);e=Math.floor(e),tempElement.innerHTML=e+"°<span>F</span>",weather.temperature.unit="fahrenheit"}else tempElement.innerHTML=weather.temperature.value+"°<span>C</span>",weather.temperature.unit="celsius"}));